Script.script:
local borderwidth = 200
setBorderRight(borderwidth)

local roomContainer = Geyser.Container:new({
  name = "RoomContents",
  color = "black",
  x = -borderwidth, y = 0,
})

local inHere = Geyser.Label:new({
  name = "InHere",
  x = 5, y = 0,
  width = "100%", height = "100%",
  fgColor = "White",

  fontSize = 12;
  message = [[In Here:]]
}, roomContainer)

inHere:setStyleSheet([[
  qproperty-alignment: 'AlignTop';
  background-color: 'black';
]])
